add_action('wp_enqueue_scripts', function () {
    // Custom CSS (loads last, already fixed)
    wp_enqueue_style(
        'pharma-main',
        'https://cdn.jsdelivr.net/gh/milanmedicals/dg-theme-assets@main/main.min.css',
        [],
        time()
    );

    // Custom JS (load after Astra + jQuery)
    wp_enqueue_script(
        'pharma-main',
        'https://cdn.jsdelivr.net/gh/milanmedicals/dg-theme-assets@main/main.min.js',
        ['jquery'],
        time(),
        true
    );
}, PHP_INT_MAX);
